    'DisplayName','rot y');
set(plot1(6),'LineWidth',2,'LineStyle','--','Color',[0 0 1],...
    'DisplayName','rot z');

% Create xlabel
xlabel('Time');

% Create ylabel
ylabel('mm of change');

% Create title
title({ChartName});

% Create legend
legend(axes1,'show');

